function buyDetail(t,a,e,l,i,s){$("#p-buydetail").find(".a-content").find("*").remove(),$.ajax({type:"post",url:luanmingli.getUrl+"getMyTreasureDetail",data:{userId:luanmingli.userId,treasureId:t,v:luanmingli.srvVersion},async:!0,dataType:"json",success:function(t){buyDetailFillData(t,a,e,l,i,s)}})}function buyDetailFillData(t,a,e,l,i,s){$("#p-buydetail").find(".a-content").append('<div class="buy-detail-hd"><p>'+decodeURIComponent(a)+"</p><p>期号：第"+e+"期</p>"+timeLucky(l,i)+'</div><p class="buy-detail-md">我已参与<span class="a8">'+s+'</span>人次，以下是所有夺宝记录</p><div class="buy-detail-bt list-block"><ul class="a-detail-ul"><li class="item-content title-text"><div class="item-inner"><div class="item-title">夺宝时间</div><div class="item-after"><span class="right-text">参与人次</span><span class="right-text">操作</span></div></div></li></ul></div>'),$.each(t.participantInfoList,function(t,a){$("#p-buydetail").find(".a-detail-ul").append('<li class="item-content content-text"><div class="item-inner"><div class="item-title">'+a.cmtTime+'</div><div class="item-after"><span class="right-text"><span class="a8">'+a.buyCount+"</span>人次</span><span onclick=\"buyDetailShowAllLuckNum('"+a.luckyNumber+'\');" class="right-text" style="color:#359df5;">查看号码</span></div></div></li>')})}function timeLucky(t,a){return t&&a?"<p>揭晓时间："+t+'</p><p>本期幸运号码：<span class="a8">'+a+"</span></p>":""}function buyDetailShowAllLuckNum(t){var a=t.split("&"),e='<div class="popup popup-luckycode close-popup"><p class="text-center" style="margin-top:.6rem;margin-bottom:.3rem;color:#5d5d5d;">幸运码</p><div style="height:7.2rem;overflow:auto;"><ul class="a-db-luckyNum-box row no-gutter list-block z_lucky_code text-center" style="list-style:none;margin:0;padding:0;color:#b0b0b0;"></ul></div></div>';$.popup(e),$.each(a,function(t,a){$("<li></li>",{class:"col-25",text:a}).appendTo(".a-db-luckyNum-box")}),$(".popup-overlay").click(function(){$.closeModal(".popup")})}function buydetailRefresh(){var t=$.getUrlParam("treasureId"),a=$.getUrlParam("name"),e=$.getUrlParam("phase"),l=$.getUrlParam("time"),i=$.getUrlParam("lucky"),s=$.getUrlParam("count");buyDetail(t,a,e,l,i,s)}$(function(){try{var t=$.getUrlParam("treasureId"),a=$.getUrlParam("name"),e=$.getUrlParam("phase"),l=$.getUrlParam("time"),i=$.getUrlParam("lucky"),s=$.getUrlParam("count");if($("#p-buydetail").find(".content-text").length>0)return;buyDetail(t,a,e,l,i,s),dropRefresh("#p-buydetail",buydetailRefresh)}catch(t){}});