function loadFinish(){setTimeout(function(){$.hideIndicator()},500)}$(function(){$.showIndicator();var i=$.getUrlParam("imgurl");if(2==$.getUrlParam("goodstype")){if(i="img/help-chest.png",$("#p-goods-detail").find(".content").find("img").length>0)return;$("#p-goods-detail").find(".content").append('<img onload="loadFinish();" src="'+i+'" />')}else{if($("#p-goods-detail").find(".content").find("iframe").length>0)return;$("#p-goods-detail").find(".content").append('<iframe onload="loadFinish();" style="border:0;width:100%;height:100%;" src="'+i+'"></iframe>')}});