function checkBonus(){$.ajax({type:"post",url:lData.getUrl+"userDetail",data:{v:lData.srvVersion,content:encryptByDES(JSON.stringify({userId:window.localStorage.getItem("userId")}))},async:!1,dataType:"json",success:function(s){var e=s.userInfo.hongbaoList,n=$.parseJSON(window.sessionStorage.getItem("bunusList")),a=[];$.each(n,function(e,n){a.push(s.hongbaoId)});var t=[];e.length>n.length&&$.map(e,function(s){$.inArray(s.hongbaoId,a)==-1&&t.push(s)}),alertMsg(t)}})}function alertMsg(s){if(0!=s.length){var e=s[0];$("#p-recharge-success").append('<div class="r-s-overlay"><div class="r-s-close"></div><div class="r-s-ct"><div class="r-s-bonus"><div class="r-s-left">¥ <span>'+e.disCount+'</span></div><div class="r-s-right"><p class="text1">'+e.hongbaoName+'</p><p class="text2">满'+e.usePoint+"元可用</p></div></div></div></div>"),$(".r-s-close").click(function(){$(".r-s-overlay").remove()})}}$(function(){window.sessionStorage.removeItem("pointsNum"),$("#p-recharge-success").find(".p-backhome").click(function(){$.router.load("personal.html")}),checkBonus()});