function shareHelpBorder(){var e=0,t=$(".text-box .ball3-ct").offset().top+$(".text-box .ball3-ct").height()/2+-$(".text-box .text1").offset().top,r=setInterval(function(){e+=10,$(".p-share-help .text-border").height(e),e>=t&&clearInterval(r)},10)}function shareHelpLink(){$(".share-send-btn").click(function(){var e=$.getUrlParam("treasureId");$.router.load("share-send.html?treasureId="+e+"&goodsinfo="+$.getUrlParam("goodsinfo"),!0)})}$(function(){try{shareHelpBorder(),shareHelpLink()}catch(e){}});